function estimateTokens(text) {
    // Simple estimation: ~0.75 tokens per word + adjustments
    const words = text.split(/\s+/).length;
    const codeBlocks = (text.match(/```[\s\S]*?```/g) || []).length;
    return Math.ceil(words * 0.75) + (codeBlocks * 10);
}
